import math

def consec(a):
    tot = 1
    big = float('inf')
    neg = 0
    for guy in a:
        tot += abs(guy)
        if abs(guy) < big:
            big = abs(guy)
        if guy < 0:
            neg += 1
    if neg % 2 == 0:
        return (tot, tot - 2 * big)
    return (tot - 2 * big, tot)

def max_possible_sum(a, B):
    n = len(a)
    m = len(B)
    g = B[0]
    for i in range(1, m):
        g = math.gcd(g, B[i])
    if g == 1:
        return sum((abs(guy) for guy in a))
    tot = 0
    tot1 = 0
    for i in range(g):
        guy = []
        j = i
        while j < n:
            guy.append(a[j])
            j += g
        (x, y) = consec(guy)
        tot += x
        tot1 += y
    return max(tot, tot1)

