def calculate_minimum_time(n: int, m: int) -> int:

    def fast_power(base: int, exp: int, mod: int) -> int:
        res = 1
        while exp > 0:
            if exp % 2 == 1:
                res = res * base % mod
            base = base * base % mod
            exp //= 2
        return res
    return (fast_power(3, n, m) - 1 + m) * m

