import math

def minimum_watering_days(n, heights):
    p = max(heights)
    s = 1
    s2 = 0
    o = 0
    for i in range(n):
        s += p - heights[i]
        s2 += p + 1 - heights[i]
        if (p - heights[i]) % 2 == 1:
            o += 1
    ans = 2 * (s // 3) + s % 3
    if o > s // 3 + 1:
        ans = o * 2 - 1
    ans2 = 2 * (s2 // 3) + s2 % 3
    if n - o > s2 // 3 + 1:
        ans2 = (n - o) * 2 - 1
    return min(ans, ans2)

