def calculate_sum_after_operations(N, M, questions):
    l = N
    f = 2
    s = N * (N + 1) // 2 - l - f
    results = []
    for k in questions:
        if 2 <= k <= N - 1 or k in [f, l]:
            (l, f) = (f, l)
        else:
            l = k
        results.append(s + l + f)
    return results

