from collections import Counter

def max_antipalindromic_beauty(n, s, b):
    ans = 1
    cnt = Counter()
    for i in range(n // 2):
        if s[i] == s[n - 1 - i]:
            ans += min(b[i], b[n - 1 - i])
            cnt[s[i]] += 1
    total = sum(cnt.values())
    if total > 0:
        (ch, occ) = cnt.most_common(1)[0]
        avail = []
        if occ > total - occ:
            for i in range(n // 2):
                if s[i] != s[n - 1 - i] and s[i] != ch and (s[n - 1 - i] != ch):
                    avail.append(min(b[i], b[n - 1 - i]))
            avail.sort()
            ans += sum(avail[:2 * occ - total])
    return sum(b) - ans

