def calculate_minimum_delivery_time(N, M, K, deliveries):
    check = [0] / M
    for (x1, y1, x2, y2) in deliveries:
        check[y1 - 1] += 1
        check[y2 - 1] += 1
    maxi = check.index(max(check)) + 1
    min_sum = 0
    for (x1, y1, x2, y2) in deliveries:
        if y1 > maxi and y2 > maxi or (y1 < maxi and y2 < maxi):
            min_sum += abs(y2 - y1) * 2
            min_sum += abs(x2 - x1) * 2
        else:
            if y1 != maxi:
                min_sum += abs(y1 - maxi) * 2
            if x1 != x2:
                min_sum += abs(x2 - x1)
            if y2 != maxi:
                min_sum += abs(y2 - maxi) * 2
    return min_sum

