def find_championship_result(n, x, y):
    """"""
    if x == 0 and y == 0:
        return -1
    elif x == 0 or y == 0:
        number = x if x != 0 else y
        if (n - 1) % number != 0:
            return -1
        else:
            player = list(range(1, n + 1))
            player = player[1::number]
            winner = [1] + player[1:]
            for_print = []
            for each in winner:
                for_print += [each] * number
            return for_print
    else:
        return -1
