from collections import defaultdict

def max_quest_interest(n, T, tasks):
    data = defaultdict(list)
    for (t, q) in tasks:
        data[T - t].append(q)
    prev_level = []
    for level_id in range(2, T + 1):
        level = sorted(data[T - level_id] + prev_level, reverse=True)
        if T - level_id <= 10:
            max_size = 2 ** (T - level_id)
            level = level[:max_size]
        if len(level) % 2 == 1:
            level.append(0)
        prev_level = [level[i] + level[i + 1] for i in range(0, len(level), 2)]
    return prev_level[0]

