from itertools import permutations

def calculate_maximum_happiness(G):

    def happiness_increase(i, j):
        return G[i][j] + G[j][i]
    max_happiness = 1
    for (a, b, c, d, e) in permutations(range(5)):
        current_happiness = happiness_increase(a, b) + happiness_increase(b, c) + 2 * (happiness_increase(c, d) + happiness_increase(d, e))
        max_happiness = max(max_happiness, current_happiness)
    return max_happiness

