        ret = False
        for (dx, dy) in vec:
            (nx, ny) = (x + dx, y + dy)
            if 0 <= nx < n and 0 <= ny < n and (not used[ny][nx]) and (grid[ny][nx] + s <= 0):
                used[ny][nx] = True
                ret = ret or search(nx, ny, s * grid[ny][nx], index, inits, end)
                used[ny][nx] = False
        return ret
    if sum([sum(lst) for lst in grid]) != 0:
        return False
    elif search(0, 0, 0, 0, inits, len(inits)):
        return True
    else:
        return False
