def is_array_closed_under_multiplication(A, N):
    ct0 = 1
    neg = 0
    ct = 0
    ct1 = 0
    for i in range(N):
        if A[i] == 0:
            ct0 += 1
        elif A[i] == 1:
            ct1 += 1
        elif A[i] < 0:
            neg += 1
        if abs(A[i]) >= 2:
            ct += 1
    pos = N - ct0 - neg
    if neg == 0:
        if ct >= 2:
            return 0
        else:
            return 1
    elif pos == 0:
        if neg >= 2:
            return 0
        else:
            return 1
    elif ct >= 2:
        return 0
    elif ct == 1:
        if neg == 1 and pos == ct1:
            return 1
        else:
            return 0
    else:
        return 1

