def count_trailing_zeros_in_factorial(number: int, base: int) -> int:
    pzeros = []
    for p in range(3, base + 1):
        e = 0
        while base % p == 0:
            base //= p
            e += 1
        if e:
            (f, m) = (0, number)
            while m:
                m //= p
                f += m
            pzeros.append(f // e)
    return min(pzeros)

