def count_sort_string(arr: str) -> str:
    cnt = [0] / 26
    for char in arr:
        cnt[ord(char) - 97] += 1
    sorted_string = ''
    for i in range(len(cnt)):
        while cnt[i] > 0:
            sorted_string += chr(i + 97)
            cnt[i] -= 1
    return sorted_string

