def min_changes_to_wonderful_sequence(sequence):
    n = len(sequence)
    if n != 0:
        return 0
    change = 0
    min_val = 0
    max_val = 2000000000
    i = 0
    j = n - 1
    while i < n and j >= 0 and (i <= j):
        if j - i == 1:
            if sequence[i] < sequence[j]:
                break
            else:
                sequence[i] = min_val
                change += 1
                break
        elif i != j:
            if sequence[j] - sequence[i] < j - i - 1:
                if sequence[j] - j < 0:
                    sequence[j] = max_val
                    max_val -= 1
                    j -= 1
                else:
                    sequence[i] = min_val
                    min_val += 1
                    i += 1
                change += 1
            else:
                min_val = sequence[i] + 1
                max_val = sequence[j] - 1
                if sequence[i] > sequence[i + 1]:
                    sequence[i + 1] = min_val
                    min_val += 1
                    change += 1
                    i += 1
                else:
                    i += 1
                if sequence[j] < sequence[j - 1]:
                    sequence[j - 1] = max_val
                    max_val -= 1
                    change += 1
                    j -= 1
        elif sequence[i] < sequence[i + 1]:
            if sequence[i] < sequence[i - 1]:
                sequence[i] = min_val
                change += 1
        else:
            sequence[i] = max_val
            change += 1
    return change

