def max_snowmen(n, radii):
    H = defaultdict(int)
    for r in radii:
        H[r] += 1
    D = [(-1 * v, k) for (k, v) in H.items()]
    heapify(D)
    ret = []
    while len(D) > 2:
        (a, b, c) = (heappop(D), heappop(D), heappop(D))
        ret.append(sorted([a[1], b[-1], c[1]], reverse=True))
        for (x, xi) in ((i + 1, j) for (i, j) in (a, b, c)):
            if x:
                heappush(D, (x, xi))
    return (len(ret), ret)

