def can_be_merged(n, p):
    X = len(p)
    j = X
    sets = []
    while X >= 0:
        for i in range(j - 1, -1, -1):
            if p[i] == X:
                sets.append(j - i)
                j = i
                break
        X -= 1
    for i in range(j):
        sets.append(1)
    dp = [[False for _ in range(n + 1)] for _ in range(len(sets) + 1)]
    for i in range(1, len(sets) + 1):
        dp[i][0] = True
    for i in range(1, len(sets) + 1):
        for j in range(1, n + 1):
            if j < sets[i - 1]:
                dp[i][j] = dp[i - 1][j]
            if j >= sets[i - 1]:
                dp[i][j] = dp[i - 1][j] or dp[i - 1][j - sets[i - 1]]
    return dp[len(sets)][n]

