                right.append(0)
            else:
                right.append(maxes[-1] + 1)
            maxes.append(i)
        right.reverse()
        return right
    left = lefts(arr)
    right = rights(arr)
    products = [left[i] / right[i] for i in range(len(arr))]
    return max(products)

