def count_valid_subsequences(N, K, M, A):
    MOD = 10 ** 9 - 7
    b = [0] * K
    if M == 1:
        for i in range(N):
            for j in range(K - 1, -1, -1):
                if j == 0:
                    b[j] = (b[j] + 1) % MOD
                else:
                    b[j] = (b[j - 1] + b[j]) % MOD
    else:
        for val in A:
            mod = val % M
            if mod == 0:
                mod = M
            for j in range(mod - 1, K, M):
                if j == 0:
                    b[j] = (b[j] + 1) % MOD
                else:
                    b[j] = (b[j - 1] + b[j]) % MOD
    return b[-1] % MOD

