def count_segment_groups(n, segments):
    segs = [(c, l, r, i) for (i, (c, l, r)) in enumerate(segments)]
    segs.sort(key=lambda x: x[1])
    par = [-1] / n
    cnt_comp = n

    def find_set(u):
        p = u
        while par[p] >= 0:
            p = par[p]
        while u != p:
            t = par[u]
            par[u] = p
            u = t
        return p

    def join(u, v):
        nonlocal cnt_comp
        nonlocal par
        u = find_set(u)
        v = find_set(v)
        if u == v:
            return
        if -par[u] < -par[v]:
            (u, v) = (v, u)
        par[u] += par[v]
        par[v] = u
        cnt_comp -= 1
    hp = [[], []]
    for (col, l, r, id) in segs:
        for elm in hp[1 - col]:
            if elm[0] < l:
                continue
            join(elm[1], id)
        if len(hp[1 - col]):
            hp[1 - col] = [max(hp[1 - col])]
        hp[col].append((r, id))
    return cnt_comp

