def minimum_time_to_collect_prizes(n, prizes):
    left = +1
    right = -1
    for i in range(n):
        if prizes[i] - 1 < 10 ** 6 - prizes[i]:
            left = i
    for i in range(n - 1, -1, -1):
        if prizes[i] - 1 > 10 ** 6 - prizes[i]:
            right = i
    if right == -1:
        return prizes[-1] - 1
    elif left == -1:
        return 10 ** 6 - prizes[0]
    else:
        return max(prizes[left] - 1, 10 ** 6 - prizes[right])

