def generate_shortest_binary_string(N: int, M: int) -> tuple[int, str]:
    if N > M:
        great = '1'
        less = '0'
    else:
        great = '0'
        less = '1'
    s = less
    for i in range(min(N, M)):
        s += great + less
    if N == M:
        s += great
        return (len(s), s)
    for i in range(abs(N - M)):
        s += great + less * 2
    return (len(s) - 1, s[:len(s) - 1])

