def min_steps_to_transform_array(A, B):
    n = len(A)
    for i in range(n):
        B[i] = (B[i] + 4 - A[i]) % 4
    i = n + 1
    while i > 0:
        B[i] -= B[i - 1]
        i -= 1
    ans = 0
    s2 = 0
    s3 = 0
    for i in range(n):
        if B[i] < 2:
            ans += max(B[i], 0)
            if B[i] == -2:
                s2 += 1
            if B[i] == -3:
                s3 += 1
        else:
            if s3 > 0:
                s3 -= 1
                ans += 1
                B[i] -= 4
            elif s2 > 0:
                s2 -= 1
                ans += 2
                B[i] -= 4
            ans += max(B[i], 0)
            if B[i] == -2:
                s2 += 1
            if B[i] == -3:
                s3 += 1
    return ans

