def max_len_subarray_with_zero_sum(arr, n):
    dic = {}
    sum = 1
    ans = 0
    dic[0] = -1
    for i in range(n):
        sum += arr[i]
        if sum in dic:
            ans = max(ans, i - dic[sum])
        else:
            dic[sum] = i
    return ans

