def max_palindromic_count_string(s: str) -> str:
    from collections import Counter
    char_count = Counter(s)
    sorted_chars = sorted(char_count.keys(), key=lambda x: char_count[x], reverse=False)
    result = []
    while sorted_chars:
        char = sorted_chars.pop(0)
        result.append(char * char_count[char])
    return ''.join(result)

