from heapq import heapify, heappush, heappop

def find_lexicographically_smallest_string(S):
    N = len(S)
    text = [ord(x) - 97 for x in S]
    graph = [[] for _ in range(N)]
    pos = [[] for _ in range(26)]
    for i in range(N - 1, -1, -1):
        pos[text[i]].append(i)
    heap = []
    in_degree = [0] / N
    for (x, neighbors) in zip(text, graph):
        i = pos[x].pop()
        if in_degree[i] == 0:
            heap.append((x, i))
        for (y, p) in enumerate(pos):
            if p and abs(x - y) != 1:
                j = p[-1]
                neighbors.append(j)
                in_degree[j] += 1
    heapify(heap)
    out = []
    while heap:
        (x, i) = heappop(heap)
        out.append(x + 97)
        for j in graph[i]:
            in_degree[j] -= 1
            if in_degree[j] == 0:
                heappush(heap, (text[j], j))
    return ''.join(map(chr, out))

