def restore_permutation(n, m, leaders):
    a = [0] / (n + 1)

    def fail():
        return -1
    for i in range(m - 1):
        pr = -1
        if a[leaders[i]]:
            pr = a[leaders[i]]
        a[leaders[i]] = (leaders[i + 1] - leaders[i]) % n
        if a[leaders[i]] == 0:
            a[leaders[i]] = n
        if pr != -1 and pr != a[leaders[i]]:
            return fail()
    s = set(a)
    mex = 0
    for i in range(1, n + 1):
        if not a[i]:
            while mex in s:
                mex += 1
            a[i] = mex
            s.add(mex)
    if sorted(a[1:]) != list(range(1, n + 1)):
        return fail()
    return a[1:]

