import math

def calculate_salesman_tours(N, roads):
    adj = [[] for _ in range(N)]
    for (a, b) in roads:
        adj[a].append(b)
        adj[b].append(a)
    facts = [0] / N
    z = 0
    for i in range(N):
        vertList = adj[i]
        z_count = 0
        nz_count = 0
        for neighbor in vertList:
            if len(adj[neighbor]) == 1:
                facts[i] += 1
                z_count += 1
                z += 1
            else:
                nz_count += 1
            if nz_count > 2:
                return 0
    sum_fact = 1
    for num in facts:
        sum_fact = sum_fact * math.factorial(num) % 1000000007
    if z == N - 1:
        return sum_fact
    return 2 * sum_fact % 1000000007

