        l = len(str1)
        if l == 1:
            return abs(ord(str1[0]) - ord(str2[0]))
        minnum = float('inf')
        minnum = min(minnum, minoper(str1[1:], str2[1:]) + abs(ord(str1[0]) - ord(str2[0])))
        for i in range(1, l):
            str11 = list(str1)
            str22 = list(str2)
            (str22[0], str22[i]) = (str22[i], str22[0])
            minnum = min(minnum, 1 + minoper(str11[1:], str22[1:]) + abs(ord(str11[0]) - ord(str22[1])))
        return minnum
    return minoper(list(str1), list(str2))
