def minimum_operations_to_equalize_chocolates(test_cases):

    def mod(x):
        return x if x > 0 else -1 * x

    def funct(tmp):
        x = 0
        if tmp >= 5:
            (x, tmp) = divmod(tmp, 5)
        if tmp >= 2:
            (a, tmp) = divmod(tmp, 2)
            x += a
        x -= tmp
        return x
    results = []
    for A in test_cases:
        N = len(A)
        mins = sorted(A)[0]
        sums = [0] * 6
        for j in range(6):
            sums[j] = 0
            for i in range(N):
                tmp = mod(A[i] - (mins - j))
                sums[j] += funct(tmp)
        min_operations = min(sums)
        results.append(min_operations)
    return results

