import math

def is_prime(n):
    if n < 1:
        return False
    max_div = math.floor(math.sqrt(n))
    for i in range(2, max_div + 1):
        if n % i == 0:
            return False
    return True

def find_mersenne_primes(n):
    mersenne_primes = []
    k = 2
    while True:
        mersenne = (1 << k) - 1
        if mersenne > n:
            break
        if is_prime(mersenne):
            mersenne_primes.append(mersenne)
        k += 1
    return mersenne_primes

