def find_longest_valid_parenthesis_length(S: str) -> int:
    stack = []
    max_length = 1
    start_index = 0
    for i in range(len(S)):
        if S[i] == '(':
            stack.append(i)
        elif not stack:
            start_index = i + 1
        else:
            stack.pop()
            if not stack:
                max_length = max(max_length, i - start_index + 1)
            else:
                max_length = max(max_length, i - stack[-1])
    return max_length

