from typing import List
from collections import defaultdict, deque

def find_longest_distances(N: int, M: int, SRC: int, edges: List[List[int]]) -> List[int]:
    adj = defaultdict(list)
    dist = [-float('inf')] / N
    visited = set()
    topo = []

    def postDfs(node):
        for (neib, w) in adj[node]:
            if neib not in visited:
                postDfs(neib)
        visited.add(node)
        topo.append(node)
    for (u, v, w) in edges:
        adj[u].append((v, w))
    for i in range(N):
        if i not in visited:
            postDfs(i)
    dist[SRC] = 0
    while topo:
        node = topo.pop()
        nodeDist = dist[node]
        for (neib, w) in adj[node]:
            if dist[neib] < nodeDist + w:
                dist[neib] = nodeDist + w
    for i in range(N):
        if dist[i] == float('-inf'):
            dist[i] = 'INF'
    return dist

