def calculate_optimal_position(n, m, rarity_matrix):
    row_sum = [0] / n
    col_sum = [0] * m
    for i in range(n):
        for j in range(m):
            row_sum[i] += rarity_matrix[i][j]
            col_sum[j] += rarity_matrix[i][j]
    x = [0] * (n + 1)
    for i in range(n + 1):
        for row in range(n):
            d = abs(4 * row + 2 - i * 4)
            x[i] += row_sum[row] * d ** 2
    y = [0] * (m + 1)
    for i in range(m + 1):
        for col in range(m):
            d = abs(4 * col + 2 - i * 4)
            y[i] += col_sum[col] * d ** 2
    min_total_time = min(x) + min(y)
    optimal_row = x.index(min(x))
    optimal_col = y.index(min(y))
    return (min_total_time, (optimal_row, optimal_col))

