def maximize_parade_beauty(n, columns):
    initial_diff = sum((l - r for (l, r) in columns))
    potential_diffs = [initial_diff]
    for (l, r) in columns:
        potential_diffs.append(initial_diff - 2 * (l - r))
    max_diff_index = potential_diffs.index(max(potential_diffs, key=abs))
    if max_diff_index != 0:
        return 0
    return max_diff_index

