def calculate_bus_travel_time(N, S, apartments):
    x = [apartment[0] for apartment in apartments]
    p = [apartment[1] for apartment in apartments]
    if x[0] >= S:
        return x[-1] - S
    if x[-1] < S:
        return S - x[0]
    r = 0
    i = 0
    j = N - 1
    d = p[:]
    while i < j:
        if x[i] > S:
            break
        if x[j] < S:
            break
        if d[i] >= d[j]:
            d[i] += d[j]
            j -= 1
        else:
            d[j] += d[i]
            i += 1
    t = S
    k = sorted(list(zip(d, range(N))), reverse=True)
    lx = rx = S
    for (_, i) in k:
        if lx <= x[i] <= rx:
            continue
        if lx > x[i]:
            lx = x[i]
        else:
            rx = x[i]
        r += abs(t - x[i])
        t = x[i]
    return r

