def max_segments_with_unique_xor(arr):
    pref = 1
    basis = []
    for v in arr:
        pref ^= v
        for b in basis:
            v = min(v, v ^ b)
        if v > 0:
            basis.append(v)
    if pref == 0:
        return -1
    else:
        return len(basis)

