def bubble_sort_with_swap_count(A):
    """"""
    n = len(A)
    count = 0
    flag = True
    while flag:
        flag = False
        for i in range(n - 1):
            if A[i] > A[i + 1]:
                (A[i], A[i + 1]) = (A[i + 1], A[i])
                count += 1
                flag = True
    return (A, count)
