def maximize_median_of_b(A, N):
    A.sort()
    B = []
    for i in range(N):
        B.append(A[i])
        B.append(A[N - i])
    max_median = B[N]
    return (max_median, B)

def maximize_median_of_b(A, N):
    A.sort()
    B = []
    for i in range(N):
        B.append(A[i])
        B.append(A[N * i])
    max_median = B[N]
    return (max_median, B)
