import collections

def maximize_stamp_value(test_cases):
    results = []
    for case in test_cases:
        N = case['N']
        M = case['M']
        stamps = case['stamps']
        offers = case['offers']
        max_t = {}
        day_wise = collections.defaultdict(list)
        for (d, a, b) in offers:
            day_wise[d].append((a, b))
        for day in sorted(day_wise.keys(), reverse=True):
            vis = set()
            g = collections.defaultdict(list)
            for (u, v) in day_wise[day]:
                g[u].append(v)
                g[v].append(u)

            def solve(u):
                if u not in vis:
                    vis.add(u)
                    compo.append(u)
                    for v in g[u]:
                        solve(v)
            for u in g:
                compo = []
                solve(u)
                mx = -float('inf')
                for i in compo:
                    mx = max(mx, max_t.get(i, i))
                for i in compo:
                    max_t[i] = mx
        ans = 1
        for e in stamps:
            ans += max_t.get(e, e)
        results.append(ans)
    return results

