def can_turn_on_all_bulbs(K, M, button_bulbs):
    total_sum_needed = M * (M + 1) / 2
    turned_on_bulbs = set()
    for button in button_bulbs:
        num_bulbs = button[0]
        for bulb in button[1:]:
            if bulb not in turned_on_bulbs:
                total_sum_needed -= bulb
                turned_on_bulbs.add(bulb)
            if total_sum_needed == 0:
                return 'YES'
    return 'NO'

