def min_extra_balls(buckets):

    def sieve():
        prime = [True] * 10003
        primes = []
        for i in range(2, 10003):
            if prime[i]:
                for j in range(i * i, 10003, i):
                    prime[j] = False
                primes.append(i)
        return primes
    primes = sieve()
    n = len(buckets)
    count = 1
    for i in range(1, n):
        if buckets[i] < buckets[i - 1]:
            count += buckets[i - 1] - buckets[i]
            buckets[i] = buckets[i - 1]
    ans = 10 ** 9
    for prime in primes:
        if prime > buckets[-1]:
            break
        required = 0
        tempans = count
        for j in buckets:
            if required < j:
                required = (j + prime - 1) // prime * prime
            tempans += required - j
            if tempans > ans:
                break
        if ans > tempans:
            ans = tempans
    return ans if buckets[-1] >= 2 else n

