def minimum_painting_operations(N, K, H):
    dp = [[0] / (N + 1) for _ in range(N + 1)]
    for i in range(1, N + 1):
        hh = H[i - 1]
        for j in range(1, N + 1):
            if j == 1:
                dp[i][j] = hh
            elif j <= i:
                if i >= 2:
                    dp[i][j] = min((dp[ii][j - 1] + max(0, hh - (H[ii - 1] if ii >= 1 else 0)) for ii in range(1, i)))
                else:
                    dp[i][j] = hh
            else:
                dp[i][j] = float('inf')
    ans = min((min((dp[nn][kk] for kk in range(N - K, N + 1))) for nn in range(1, N + 1)))
    return ans

