def calculate_shortest_path_sums(n, adj_matrix, deletion_order):
    deletion_order = [x - 1 for x in deletion_order]
    ans = [''] / n
    for i in range(n - 1, -1, -1):
        x = deletion_order[i]
        for a in range(n):
            for b in range(n):
                if adj_matrix[a][b] > adj_matrix[a][x] + adj_matrix[x][b]:
                    adj_matrix[a][b] = adj_matrix[a][x] + adj_matrix[x][b]
        lower = 0
        higher = 0
        for a in deletion_order[i:]:
            for b in deletion_order[i:]:
                lower += adj_matrix[a][b]
                if lower > 10 ** 9:
                    higher += 1
                    lower -= 10 ** 9
        ans[i] = str(10 ** 9 * higher + lower)
    return [int(x) for x in ans]

