def generate_word_index(word_page_pairs):
    index = {}
    for (word, page) in word_page_pairs:
        if word not in index:
            index[word].append(page)
        else:
            index[word] = [page]
    for word in index:
        index[word].sort()
    sorted_index = dict(sorted(index.items()))
    return sorted_index
