def calculate_max_costs(N, items):
    ones = []
    twos = []
    for (W, C) in items:
        if W == 1:
            ones.append(C)
        else:
            twos.append(C)
    max_weight = len(ones) - 2 * len(twos)
    if len(ones) >= 1:
        ones.append(0)
        ones.sort(reverse=True)
        even_twos = twos.copy()
        odd_twos = twos.copy()
        for i in range(1, len(ones)):
            if i % 2 == 1:
                even_twos.append(ones[i] + ones[i - 1])
            else:
                odd_twos.append(ones[i] + ones[i - 1])
        even_twos = sorted(even_twos)
        odd_twos = sorted(odd_twos)
        res = [0, ones[0]]
        for w in range(2, max_weight + 1):
            if w % 2 == 0:
                temp = even_twos.pop()
            else:
                temp = odd_twos.pop()
            res.append(res[w - 2] + temp)
    else:
        res = [0, 0]
        twos = sorted(twos)
        for w in range(2, max_weight + 1):
            temp = 0
            if w % 2 == 0:
                temp = twos.pop()
            res.append(max(res[w - 2] + temp, res[w - 1]))
    res.pop(0)
    return res

