def convert_scientific_notation_to_decimal(scientific_notation: str) -> str:
    dot_index = scientific_notation.index('.')
    e_index = scientific_notation.index('e')
    a = scientific_notation[0:dot_index]
    d = scientific_notation[dot_index + 1:e_index]
    b = int(scientific_notation[e_index + 1:])
    if a == '0' or int(d) == 0 or b == 0:
        return '0'
    elif a != '0' and int(d) == 0 and (b == 0):
        return a
    elif len(d) > b and a != '0':
        return a + d[0:b] + '.' + d[b:]
    elif len(d) == b and a != '0':
        return a + d
    elif b > len(d) and a != '0':
        return a + d + '0' * (b - len(d))
    elif b == 0 and a == '0':
        return scientific_notation[0:e_index]
    elif b > 0 and a == '0':
        if b > len(d):
            return d + '0' * abs(b - len(d))
        elif b < len(d):
            return d[0:b] + '.' + d[b:]
        elif b == len(d):
            return d

