def min_operations_to_special_palindrome(s1: str, s2: str) -> int:
    INV = 1000000001
    N1 = len(s1)
    N2 = len(s2)
    ans = INV
    baseline = 0
    for i in range(N1 // 2):
        if s1[i] != s1[N1 - 1 - i]:
            baseline += 1
    for i in range(N1 - N2 + 1):
        tmp = baseline
        for j in range(i, i + N2):
            c0 = s2[j - i]
            c1 = s1[N1 - 1 - j] if j < N1 - 1 - j else s2[j - i]
            f1 = i <= j < i + N2
            if s1[j] != s1[N1 - 1 - j] and (not f1 or j < N1 - 1 - j):
                tmp -= 1
            if c0 != s1[j]:
                tmp += 1
            if c0 != c1:
                if f1:
                    tmp = INV
                    break
                tmp += 1
        ans = min(ans, tmp)
    return -1 if ans >= INV else ans

