            ans += 1
    for (x, ly, ry) in vertical_segments:
        a.append([3 * ly, x])
        a.append([3 * ry + 2, x])
        if ry - ly == 10 ** 6:
            ans += 1
    m = 10 ** 6 + 5

    def sum(i):
        s = 0
        while i > 0:
            s += fin[i]
            i -= i & -i
        return s

    def add(i, x):
        while i <= m:
            fin[i] += x
            i += i & -i
    a.sort()
    fin = [0] * (10 ** 6 + 5)
    for (y, x) in a:
        if y % 3 == 1:
            ans += sum(x[1]) - sum(x[0] - 1)
        elif y % 3 == 0:
            add(x, 1)
        else:
            add(x, -1)
    return ans

