def calculate_min_unhappiness(n, m, unhappiness_values, friend_pairs):
    degrees = [0] / (n + 1)
    for (u, v) in friend_pairs:
        degrees[u] += 1
        degrees[v] += 1

    def delete_one():
        min_penalty = float('inf')
        for i in range(1, n + 1):
            if degrees[i] % 2 == 1:
                min_penalty = min(min_penalty, unhappiness_values[i - 1])
        return min_penalty

    def delete_two():
        min_penalty = float('inf')
        for (u, v) in friend_pairs:
            if (degrees[u] + degrees[v]) % 2 == 0:
                min_penalty = min(min_penalty, unhappiness_values[u - 1] + unhappiness_values[v - 1])
        return min_penalty
    if m % 2 == 0:
        return 0
    return min(delete_one(), delete_two())

