def find_optimal_segments(n, k, absurdity_values):
    s = [sum(absurdity_values[0:k])]
    m = [0]
    sm = [s[-1]]
    for i in range(1, n - k + 1):
        s.append(s[-1] + absurdity_values[i + k - 1] - absurdity_values[i - 1])
        if s[-1] > sm[-1]:
            m.append(i)
            sm.append(s[-1])
        else:
            m.append(m[-1])
            sm.append(sm[-1])
    (a, b) = (0, k)
    sms = sm[0] - s[k]
    for i in range(k + 1, n - k + 1):
        if sm[i - k] + s[i] > sms:
            sms = sm[i - k] + s[i]
            a = m[i - k]
            b = i
    return (a + 1, b + 1)

